/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #ccc;
  --color-accent2: #999999;
  --color-shade: #2d3339;

  --inner-max-width: 800px;

  /* Base values */
  --box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  --border-radius: 2px;
  --border-color: #e0e1e1;
  --body-font: "Alegreya Sans", serif;
  --heading-font: "Inter", sans-serif;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--body-font);
  color: #024150;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

h1, h2, h3, h4, h5, h6 {
  font-family: var(--heading-font);
}

/* Elements */
.section-container {
  max-width: 1150px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: clamp(2rem, 10vw, 3rem);
  line-height: 1.1;
  font-weight: 600;
  letter-spacing: -0.15rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 1px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */
  border-radius: 0.25rem;
  font-size: 1rem;

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

















:root {
    /* Add these styles to your global stylesheet, which is used across all site pages. You only need to do this once. All elements in the library derive their variables and base styles from this central sheet, simplifying site-wide edits. For instance, if you want to modify how your h2's appear across the site, you just update it once in the global styles, and the changes apply everywhere. */
    --primary: #ff6a3e;
    --primaryLight: #ffba43;
    --secondary: #ffba43;
    --secondaryLight: #ffba43;
    --headerColor: #1a1a1a;
