  submitting = true
  const form_data = get_form_data(e.target);
  const {status} = await axios.post(form.endpoint, form_data).catch(e => ({ status: 400 }));
  if (status === 200) {
    submitted = true;
  } else {
    error = true
  }
}

function get_form_data(form) {
  const form_data = new FormData(form);
  var object = {};
  form_data.forEach((value, key) => {
    object[key] = value;
  });
  return object;
}

